using System;
using System.Collections.Generic;
using System.Linq;
using System.Threading.Tasks;
using System.Net.Http;
using System.IO;
using Microsoft.Extensions.PlatformAbstractions;
using CodeComb.Testing.WebHost;
using PipeCI.Blob.Sample;
using Xunit;

namespace PipeCI.Blob.FunctionalTests
{
    public class BlobTests : WebHostTestFixture<Startup>
    {
        [Fact]
        public async Task upload_bytes()
        {
            // Arrange
            var env = PlatformServices.Default.Application;
            var content = new MultipartFormDataContent();
            content.Add(new StreamContent(new MemoryStream(File.ReadAllBytes(Path.Combine(env.ApplicationBasePath, "Resources/test.png")))), "file", "test.png");

            // Act 1
            var result1 = await client.PostAsync("/blob/upload", content);

            // Assert 1
            Assert.Equal(System.Net.HttpStatusCode.OK, result1.StatusCode);

            // Act 2
            var result2 = await client.GetAsync("/blob/download/" + await result1.Content.ReadAsStringAsync());

            // Assert 2
            Assert.Equal(System.Net.HttpStatusCode.OK, result2.StatusCode);
            Assert.Equal(File.ReadAllBytes(Path.Combine(env.ApplicationBasePath, "Resources/test.png")), await result2.Content.ReadAsByteArrayAsync());
        }

        [Fact]
        public async Task upload_base_64_string()
        {
            // Arrange
            var b64img = "data:image/png;base64,";

            // Act 1
            var result1 = await client.PostAsync("/blob/upload", new CustomFormUrlEncodedContent(new Dictionary<string, string>
            {
                { "file", b64img }
            }));

            // Assert 1
            Assert.Equal(System.Net.HttpStatusCode.OK, result1.StatusCode);

            // Act 2
            var result2 = await client.GetAsync("/blob/download/" + await result1.Content.ReadAsStringAsync());

            // Assert 2
            Assert.Equal(System.Net.HttpStatusCode.OK, result2.StatusCode);
            Assert.Equal(new Base64StringImage(b64img).AllBytes, await result2.Content.ReadAsByteArrayAsync());
        }
    }
}